var uniqeId = 0;

var g_friends = {};


function extract_id(ip) {
    return ip.replace(/\./g, '').replace(/\:/g, '').replace(/\//g, '').replace(/http/g, 'x')
}

function place_friend_ips(div, title, extra1, extra2){
    uniqeId++;

    AppSettings.getFriendIps(function (ips) {
        var id = 'hpanel' +uniqeId;
        var t = '<div class="host_area">' +
            '<span>'+title+'</span>' +
            '<div id="'+id+'" class="hpanel-cnt">';
        for(var i = 0; i < ips.length; i++ ){
            uniqeId++;
            var rhost = ips[i];
            if(!g_friends[rhost]){
                g_friends[rhost] = {};
            }
            var name = g_friends[rhost].name || rhost;
            t+='<div id="'+extract_id(rhost) + '_' + uniqeId +'">';
            t+='<span class="hn'+extract_id(rhost)+'">'+name+'</span>';

            if(extra1){
                t+='<button class="host-btn" onclick="'+extra1(rhost)+'">'
                t+='<img src="data:image/png;base64,"/>'
                t+='</button>'
            }

            t+='<button>'
            t+='<img src="data:image/png;base64,"/>'
            t+='</button>'

            t+='<button onclick="ping_host(\''+encodeURI(rhost)+'\')">';
            t+='<img src="data:image/png;base64,"/>'
            t+='</button>';

            t+='<button>'
            t+='<img src="data:image/png;base64,"/>'
            t+='</button>'

            t+='</div>';

        }

        if(extra2){
            t+= '<button onclick="'+extra2(rhost)+'">'
            t+='<img src="data:image/png;base64,"/>';
            t+= '</button>'
        }
        t+='</div>' +
            '<button class="host-btn" onclick="add_friend_host(\''+id+'\')">' +
            '<img src="data:image/png;base64,"/>'
            '</button>'

        t+='</div>';
        div.innerHTML = t;
    })
}

place_friend_ips(_g('ips-container'), 'Friend apps:');



function ping_host(rHost) {

    $do_request(rHost + '/device/stat', function (d) {
        console.log(d, d.N);
        if(d && d.N){
            if(!g_friends[rHost]){
                g_friends[rHost] = {
                    name: d.N
                }
            } else {
                g_friends[rHost].name = d.N;
            }

            var nodes = document.getElementsByClassName('hn' + extract_id(rHost));
            for(var i = 0; i < nodes.length; i++){
                nodes[i].innerHTML = d.N;
            }
        }
        alert('Host '+rHost+' is still running')
    });
}








